$().ready(function() {

    $("#contactForm").validate({
        rules: {
            name: "required",
            subject: "required",
            message: "required",
            email: {
                required: true,
                email: true
            },
            phone: {
                required: false,
                minlength: 8,
                maxlength: 13
            }
        },
        messages: {
            name: null,
            subject: null,
            message: null,
            email: null,
            phone: null
        },
    });
});

$("#contactForm-phone").on("keydown", function(e){
     if(!((e.keyCode > 95 && e.keyCode < 106)|| (e.keyCode > 47 && e.keyCode < 58) || e.keyCode == 8 || e.keyCode == 187)) {
        return false;
    }
});